/* eslint-disable */
import React from 'react';
import { withDeviceSize } from '../hocs';
import { loadModules } from 'esri-loader';
import { formatQuery } from 'react-querybuilder';

const MODULES = [
  'esri/Map',
  'esri/views/MapView',
  'esri/layers/FeatureLayer',
  'esri/layers/MapImageLayer',
  'esri/layers/GroupLayer',
  'esri/layers/WebTileLayer',
  'esri/Basemap',
  'esri/widgets/Legend',
  'esri/widgets/Expand',
  'esri/widgets/Print',
  'esri/widgets/Zoom',
  'esri/widgets/ScaleBar',
];

//TODO: set group layers sublayers, for direct group import.
// const setGroupLayers = async (ids, data, modules) => {
//   const { map_service_url, sublayerDatalayer, fullLayer, query = '' } =
//     data || {};
//   const { FeatureLayer, MapImageLayer, GroupLayer } = modules;

//   const glLayers = await Promise.all(
//     ids.map(async (id) => {
//       const sublayerUrl = `${map_service_url}/${id}`;
//       let sublayerData = await fetchArcGISData(sublayerUrl);
//       let sublayer;
//       switch (sublayerData.type) {
//         case 'Raster Layer':
//           sublayer = new MapImageLayer({
//             url: map_service_url,
//             minScale: sublayerData?.minScale,
//             maxScale: sublayerData?.maxScale,
//             sublayers: [
//               {
//                 id: sublayerData.id,
//                 visible: true,
//                 definitionExpression: query ? formatQuery(query, 'sql') : '',
//               },
//             ],
//           });
//           break;
//         case 'Feature Layer':
//           sublayer = new FeatureLayer({
//             layerId: sublayerData.id,
//             url: `${map_service_url}/${sublayerData.id}`,
//             definitionExpression: query ? formatQuery(query, 'sql') : '',
//             minScale: sublayerData?.minScale,
//             maxScale: sublayerData?.maxScale,
//           });
//           break;
//         default:
//           break;
//       }
//       return sublayer;
//     }),
//   );
//   return glLayers;
// };

const Webmap = (props) => {
  const { editMode, height, id } = props;

  const data = React.useMemo(() => props.data || {}, [props.data]);

  const device = React.useMemo(() => props.device || {}, [props.device]);
  const { base = {}, layers = {}, legend = {}, general = {} } = data;

  const { base_layer = '' } = base;

  const map_layers =
    layers &&
    layers.map_layers &&
    layers.map_layers
      .filter(({ map_layer }) => map_layer)
      .map((l, i) => l.map_layer);

  const options = {
    css: true,
  };

  const mapRef = React.useRef();
  const [modules, setModules] = React.useState({});

  const modules_loaded = React.useRef(false);

  React.useEffect(() => {
    if (!modules_loaded.current) {
      modules_loaded.current = true;
      loadModules(MODULES, options).then((modules) => {
        const [
          Map,
          MapView,
          FeatureLayer,
          MapImageLayer,
          GroupLayer,
          WebTileLayer,
          Basemap,
          Legend,
          Expand,
          Print,
          Zoom,
          ScaleBar,
        ] = modules;
        setModules({
          Map,
          MapView,
          FeatureLayer,
          MapImageLayer,
          GroupLayer,
          WebTileLayer,
          Basemap,
          Legend,
          Expand,
          Print,
          Zoom,
          ScaleBar,
        });
      });
    }
  }, [setModules, options]);

  //eslint-disable-next-line no-unused-vars
  const esri = React.useMemo(() => {
    if (Object.keys(modules).length === 0) return {};
    const {
      Map,
      MapView,
      FeatureLayer,
      MapImageLayer,
      GroupLayer,
      WebTileLayer,
      Basemap,
      Legend,
      Expand,
      Print,
      Zoom,
      ScaleBar,
    } = modules;
    let layers =
      map_layers && map_layers.length > 0
        ? map_layers
            .filter(({ map_service_url, layer }) => map_service_url && layer)
            .map(
              (
                { map_service_url, layer, fullLayer, query = '', opacity = 1 },
                index,
              ) => {
                const url = `${map_service_url}/${layer?.id}`;
                let mapLayer;
                switch (layer.type) {
                  case 'Raster Layer':
                    mapLayer = new MapImageLayer({
                      url: map_service_url,
                      sublayers: [
                        {
                          id: layer.id,
                          minScale: layer?.minScale,
                          maxScale: layer?.maxScale,
                          opacity: opacity ? parseFloat(opacity) : 1,
                          definitionExpression: query
                            ? formatQuery(query, 'sql')
                            : '',
                        },
                      ],
                    });
                    break;
                  case 'Feature Layer':
                    mapLayer = new FeatureLayer({
                      layerId: layer.id,
                      url,
                      definitionExpression: query
                        ? formatQuery(query, 'sql')
                        : '',
                      minScale: layer?.minScale,
                      maxScale: layer?.maxScale,
                      opacity: opacity ? parseFloat(opacity) : 1,
                    });
                    break;
                  default:
                    break;
                }
                return mapLayer;
              },
            )
        : [];

    const mapBaseLayer = new WebTileLayer({
      urlTemplate:
        'https://gisco-services.ec.europa.eu/maps/tiles/OSMPositronComposite/EPSG3857/{level}/{col}/{row}.png',
    });

    // Create a Basemap with the WebTileLayer.
    const positronCompositeBasemap = new Basemap({
      baseLayers: [mapBaseLayer],
      title: 'Positron Composite',
      id: 'positron-composite',
      thumbnailUrl:
        'https://gisco-services.ec.europa.eu/maps/tiles/OSMPositronComposite/EPSG3857/0/0/0.png',
    });

    const setBasemap = (basemap) => {
      if (basemap === 'positron-composite') {
        return positronCompositeBasemap;
      }
      if (!basemap) {
        return 'hybrid';
      }
      return basemap;
    };

    const setCustomBasemap = (urlTemplate) => {
      const mapBaseLayer = new WebTileLayer({
        urlTemplate,
      });

      // Create a Basemap with the WebTileLayer.
      const customBase = new Basemap({
        baseLayers: [mapBaseLayer],
        title: 'Custom Base Layer',
        id: 'custom-base',
      });
      return customBase;
    };

    const map = new Map({
      basemap:
        data?.base?.use_custom_base && data?.base?.custom_base_layer
          ? setCustomBasemap(data?.base?.custom_base_layer)
          : setBasemap(base_layer),
      layers,
    });
    const view = new MapView({
      container: mapRef.current,
      map,
      center:
        general?.long && general?.lat ? [general.long, general.lat] : [0, 40],
      zoom: general?.zoom_level ? general?.zoom_level : 2,
      ui: {
        components: ['attribution'],
      },
    });
    if (general && general.scalebar) {
      const scaleBarWidget = new ScaleBar({
        view: view,
        unit: general.scalebar,
      });

      view.ui.add(scaleBarWidget, {
        position: 'bottom-left',
      });
    }

    if (layers && layers[0] && general && general.centerOnExtent) {
      const firstLayer = layers[0];
      if (firstLayer.type === 'feature') {
        firstLayer
          .when(() => {
            return firstLayer.queryExtent();
          })
          .then((response) => {
            view.goTo(response.extent);
          });
      }
      if (firstLayer.type === 'map-image') {
        firstLayer.when(() => {
          view.goTo(firstLayer.fullExtent);
        });
      }
    }

    const zoomPosition =
      general && general.zoom_position ? general.zoom_position : '';

    if (zoomPosition) {
      const zoomWidget = new Zoom({
        view: view,
      });
      view.ui.add(zoomWidget, zoomPosition);
    }

    if (legend?.legend?.show_legend) {
      const legendPosition =
        legend && legend.legend && legend.legend.position
          ? legend.legend.position
          : 'top-right';

      const legendWidget = new Expand({
        content: new Legend({
          view: view,
          style: 'classic',
        }),
        view: view,
        expanded: false,
        expandIconClass: 'esri-icon-legend',
        expandTooltip: 'Legend',
        classNames: 'some-cool-expand',
      });
      view.ui.add(legendWidget, legendPosition);
    }

    const printPosition =
      general && general.print_position ? general.print_position : '';

    if (printPosition) {
      const printWidget = new Expand({
        content: new Print({
          view: view,
        }),
        view: view,
        expanded: false,
        expandIconClass: 'esri-icon-printer',
        expandTooltip: 'Print',
      });
      view.ui.add(printWidget, printPosition);
    }

    if (layers && layers.length > 0) {
      layers.forEach((layer) => {
        view.whenLayerView(layer).then((layerView) => {
          layerView.watch('updating', (val) => {});
        });
      });
    }
    return { view, map };
  }, [modules, data, data.layers, map_layers]);

  return (
    <div>
      <div
        style={{
          height:
            height && !editMode
              ? `${height}px`
              : device === 'tablet' || device === 'mobile'
              ? '300px'
              : '500px',
        }}
        ref={mapRef}
        className="esri-map"
      ></div>
    </div>
  );
};

export default withDeviceSize(React.memo(Webmap));
