      if (removeText.length === 1 && removeText === characterBeforeAnchor) {
        // Assume text should be removed right before the anchor
        return {
          anchor: { path, offset: anchor.offset - 1 },
          focus: { path, offset: anchor.offset },
        }
      }
    }
  }

  return insertionRange
}
