        receivedUserInput.current = false
        animationFrameRef.current = null
      })
    }
  }, [])

  useEffect(() => {
    // Reset user input tracking on every render
    if (receivedUserInput.current) {
      receivedUserInput.current = false
    }
  })

  return {
    receivedUserInput,
    onUserInput,
  }
}
