        [getRequestKey(action.type)]: {
          loading: true,
          loaded: false,
          error: null,
        },
      };
    case `${ZOTERO_ITEM_SAVED}_FAIL`:
      return {
        ...state,
        api: null,
        [getRequestKey(action.type)]: {
          loading: true,
          loaded: false,
          error: action.result,
        },
      };
    default:
      return state;
  }
}

export function zotero_collections(state = zoteroCollections, action = {}) {
  switch (action.type) {
    case `${ZOTERO_COLLECTIONS}_PENDING`:
      return {
        ...state,
        api: null,
        [getRequestKey(action.type)]: {
          loading: true,
          loaded: false,
          error: null,
        },
      };
    case `${ZOTERO_COLLECTIONS}_SUCCESS`:
      return {
        ...state,
        api: { ...action.result },
        totalResults: action.result.totalResults,
        [getRequestKey(action.type)]: {
          loading: true,
          loaded: false,
          error: null,
        },
      };
    case `${ZOTERO_COLLECTIONS}_FAIL`:
      return {
        ...state,
