      };
    case `${ZOTERO_SEARCH_ITEMS}_SUCCESS`:
      return {
        ...state,
        api: { ...action.result.results },
        totalResults: action.result.totalResults,
        [getRequestKey(action.type)]: {
          loading: true,
          loaded: false,
          error: null,
        },
      };
    case `${ZOTERO_SEARCH_ITEMS}_FAIL`:
      return {
        ...state,
        api: null,
        totalResults: null,
        [getRequestKey(action.type)]: {
          loading: true,
          loaded: false,
          error: action.error,
        },
      };
    default:
      return state;
  }
}

