        return [...accumulator, ...x];
      }, []);
      const totalResults = action.result.reduce((accumulator, currentValue) => {
        const x = currentValue.response?.header?.total?.$ || 0;
        return accumulator + x;
      }, 0);
      return {
        ...state,
        api,
        totalResults,
        [getRequestKey(action.type)]: {
          loading: true,
          loaded: false,
          error: null,
        },
      };
    case `${OPENAIRE_ITEMS_RSD}_FAIL`:
      return {
        ...state,
        api: null,
        totalResults: null,
        [getRequestKey(action.type)]: {
          loading: true,
          loaded: false,
          error: action.error,
        },
      };
    default:
      return state;
  }
}
